 .banners {
 text-align: center;
 display: flex;
 flex-direction: column;
 align-items: center;
 }
 .banners a.button {
 background-color: #FFC827;
 color: #2F3341;
 box-shadow: 0 5px 35px rgba(146, 146, 146, 0.2);
 padding: 20px;
 font-family: Graphic, arial;
 font-weight: 600;
 line-height: 24px;
 margin-top: -100px;
 border-radius: 3px;
 cursor: pointer;
 transition: .1s;
 }
 .banners a.button:hover {
 transform: scale(1.05);
 }
 .banners a.button a:hover,
 .banners a.button a:visited {
 color: #2F3341;
 }
 .banner-3 a.button {
 margin-left: 45%;
 }




